import React, { CSSProperties } from 'react';


import { decodeData, MultiAction } from './minibus';

const formatBalance = (value: any, e: any, o: any) => {
  return value
}
const isCfxHexAddress = (value: any) => {
  return true;
}
const AddressContainer = ({ value }: any) => {
  return <>{formatAddress(value)}</>;
}
const Link = ({ href, children }: any) => {
  return <a href={href}>{children}</a>
}
const formatAddress = (address: string) => {
  const digits = 2;

  if (!address || typeof address !== 'string') return '';
  return `${address.substring(0, digits + 2)}...${address.substring(address.length - digits)}`;
}
const LogoStyle: CSSProperties = {
  width: '16px',
  height: '16px',
  marginTop: '2px',
}
const StyleWrap: CSSProperties = {
  display: 'flex',
  flexWrap: 'wrap',
  alignContent: 'center',
  gap: '5px',
};
const BalanceStyle: CSSProperties = {
  fontWeight: 800,
};

const TransactionActionWrapper: CSSProperties = {
  maxHeight: '119px',
  overflowY: 'scroll',
}

const TokenName = 'Unknown';
const TokenIcon =
  'data:image/svg+xml;base64,';
const TokenDecimals = 18;

function filterByTokenAddress(data: any, address: any) {
  let filter: any = Object.values(data).filter(
    (item: any) => item && item.token && item.token.address === address,
  );
  if (filter && filter.length > 0) {
    return filter[0];
  } else {
    return { token: data };
  }
}
const Token = (address: string, customInfo: any, tokenType: string) => {
  const customInfoToken = filterByTokenAddress(customInfo.token || {}, address);
  return customInfoToken ? (
    <>
      {customInfoToken['token'] && customInfoToken['token']['iconUrl'] ? (
        <img style={{ ...LogoStyle }} src={customInfoToken['token'] && customInfoToken['token']['iconUrl']}
          alt="icon" />

      ) : (
        <>
          <img style={{ ...LogoStyle }} src={TokenIcon} alt="icon" />
        </>
      )}
      <Link href={`/address/${formatAddress(address)}`}>
        {customInfoToken['name'] ||
          (customInfoToken['token'] && customInfoToken['token']['name'] ? (
            `${customInfoToken['token']['name']}`
          ) : (
            <div>{TokenName}</div>
          ))}
        {(tokenType === 'ERC721' || tokenType === 'ERC1155') &&
          customInfoToken['token'] &&
          customInfoToken['token']['symbol'] &&
          `(${customInfoToken['token']['symbol']})`}
      </Link>{' '}
    </>
  ) : (
    <>
      <img style={{ ...LogoStyle }} src={TokenIcon} alt="icon" />
      <div>{TokenName}</div>
    </>
  );
};
const customUI: MultiAction = {
  ERC20_Transfer: ({ address, toAddress, value, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Transfer
        <div style={{ ...BalanceStyle }}>
          {formatBalance(value, customInfo['decimals'] || TokenDecimals, true)}
        </div>{' '}
        {Token(address, customInfo, 'ERC20')} to{' '}
        <Link href={`/address/${formatAddress(toAddress)}`}>
          <AddressContainer value={toAddress} />
        </Link>
      </div>
    );
  },
  ERC20_Approved: ({ address, toAddress, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Approved
        {Token(address, customInfo, 'ERC20')} for
        {toAddress && isCfxHexAddress(toAddress) && (
          <Link href={`/address/${formatAddress(toAddress)}`}>
            <AddressContainer value={toAddress} />
          </Link>
        )}
      </div>
    );
  },
  ERC20_Revoked: ({ address, toAddress, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Revoked
        {Token(address, customInfo, 'ERC20')} from
        {toAddress && (
          <Link href={`/address/${formatAddress(toAddress)}`}>
            <AddressContainer value={toAddress} />
          </Link>
        )}
      </div>
    );
  },
  ERC721_Mint: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Mint <div style={{ ...BalanceStyle }}>1</div> of{' '}
        {Token(address, customInfo, 'ERC721')}
      </div>
    );
  },
  ERC721_Transfer: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Transfer <div style={{ ...BalanceStyle }}>1</div> of{' '}
        {Token(address, customInfo, 'ERC721')}
      </div>
    );
  },
  ERC721_Burn: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Burn <div style={{ ...BalanceStyle }}>1</div> of{' '}
        {Token(address, customInfo, 'ERC721')}
      </div>
    );
  },
  ERC721_SafeTransferFrom: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Transfer <div style={{ ...BalanceStyle }}>{value}</div> of{' '}
        {Token(address, customInfo, 'ERC721')}
      </div>
    );
  },
  ERC721_Revoked: ({ address, toAddress, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Revoked {Token(address, customInfo, 'ERC721')}
        from
        {toAddress && isCfxHexAddress(toAddress) && (
          <Link href={`/address/${formatAddress(toAddress)}`}>
            <AddressContainer value={toAddress} />
          </Link>
        )}
      </div>
    );
  },
  ERC721_Approved: ({ address, toAddress, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Approved {Token(address, customInfo, 'ERC721')}
        for
        {toAddress && isCfxHexAddress(toAddress) && (
          <Link href={`/address/${formatAddress(toAddress)}`}>
            <AddressContainer value={toAddress} />
          </Link>
        )}
      </div>
    );
  },
  ERC1155_Approved: ({ address, toAddress, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Approved {Token(address, customInfo, 'ERC1155')}
        for
        {toAddress && isCfxHexAddress(toAddress) && (
          <Link href={`/address/${formatAddress(toAddress)}`}>
            <AddressContainer value={toAddress} />
          </Link>
        )}
      </div>
    );
  },
  ERC1155_Revoked: ({ address, toAddress, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Revoked {Token(address, customInfo, 'ERC1155')}
        for
        {toAddress && isCfxHexAddress(toAddress) && (
          <Link href={`/address/${formatAddress(toAddress)}`}>
            <AddressContainer value={toAddress} />
          </Link>
        )}
      </div>
    );
  },
  ERC1155_Mint: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Mint <div style={{ ...BalanceStyle }}>{value}</div> of{' '}
        {Token(address, customInfo, 'ERC1155')}
      </div>
    );
  },
  ERC1155_SafeTransferFrom: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Transfer <div style={{ ...BalanceStyle }}>{value}</div>{' '}
        {Token(address, customInfo, 'ERC1155')}
      </div>
    );
  },
  ERC1155_Burn: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Burn <div style={{ ...BalanceStyle }}>{value}</div>{' '}
        {Token(address, customInfo, 'ERC1155')}
      </div>
    );
  },
  ERC1155_Transfer: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Transfer <div style={{ ...BalanceStyle }}>{value}</div>{' '}
        {Token(address, customInfo, 'ERC1155')}
      </div>
    );
  },
  ERC1155_SafeBatchTransferFrom: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Transfer <div style={{ ...BalanceStyle }}>{value}</div>{' '}
        {Token(address, customInfo, 'ERC1155')}
      </div>
    );
  },
  ERC1155_BatchBurn: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Burn <div style={{ ...BalanceStyle }}>{value}</div>{' '}
        {Token(address, customInfo, 'ERC1155')}
      </div>
    );
  },
  ERC1155_BatchMint: ({ value, address, customInfo }) => {
    return (
      <div style={{ ...StyleWrap }}>
        Mint <div style={{ ...BalanceStyle }}>{value}</div> of{' '}
        {Token(address, customInfo, 'ERC1155')}
      </div>
    );
  },
};

const TransactionAction = ({ transaction, event, customInfo }: any) => {

  const res = decodeData(transaction, event, customInfo, customUI);
  return (
    <div style={{ ...TransactionActionWrapper }}>
      {res.content &&
        res.content.length > 0 &&
        res.content.map((e: any) => {
          return <div>{e}</div>;
        })}
    </div>
  );
};

export default TransactionAction;
